import React, { ChangeEvent, useState, useEffect, useCallback } from 'react';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import TextField from '@mui/material/TextField';
import Checkbox from '@mui/material/Checkbox';
import { DataGrid } from '@mui/x-data-grid';

import { AppDef, AppParams } from '../apps';


type DataType = {
  name: string,
  stats: Array< {
    base_stat: number,
    effort: number,
    stat: {
      name: string,
      url: string,
    }
  } >,
}

const appDef: AppDef = {
    id: 'pokemon',
    label: 'Pokemon',
    component: Pokemon,
}
export default appDef;

/**
 * Pokemon
 */
function Pokemon( params: AppParams ) {

  const [ isFetching, setIsFetching ] = useState<boolean>(false);

  /**
   * The name of the pokemon to retrieve
   */
  const [ pokemon, setPokemon ] = useState<string>('pikachu');

  const [ data, setData ] = useState<DataType|null>(null);

  console.log( 'Data', data );

  //
  // Callbacks
  //

  const fetchPokemon = useCallback( async () => {
    if ( ! isFetching ) {
      setIsFetching( true );
      try {
        const response = await fetch ("https://pokeapi.co/api/v2/pokemon/" + pokemon );
        const data = await response.json();

        console.log( 'Setting data', data );
        setData( data );
      } finally {
        setIsFetching( false );
      }
    }
  }, [ isFetching ]);

  const columns = [
    // { field: 'stat.name', headerName: 'Stat' },
    { field: 'base_stat', headerName: 'Base Stat' },
    { field: 'effort', headerName: 'Effort' },
  ];

  return (
    <Container>
      <Typography variant="h2" color="inherit" component="div">
      Pokemon
      </Typography>
      <Button variant='contained' onClick={ fetchPokemon }>Get Pokemon</Button>
      <TextField id="standard-basic" label="Standard" value={pokemon} onChange={ ev => setPokemon( ev.target.value) } variant="standard" />
    
      { data ? <div>
        {/* <DataGrid
          rows={data.stats}
          columns={columns}
          initialState={{
            pagination: {
              paginationModel: { page: 0, pageSize: 5 },
            },
          }}
          pageSizeOptions={[5, 10]}
        /> */}
        Yes
      </div> : null
      }
    </Container>
  );
} // END Pokemon
