import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import * as React from 'react';
import Checkbox from '@mui/material/Checkbox';
import { AppDef, AppParams } from '../apps';
import TextField from '@mui/material/TextField';




const appDef: AppDef = {
    id: 'pokemon',
    label: 'Pokemon',
    component: Pokemon,
}
export default appDef;

const masterList = [
  'apple', 'eggs', 'elephant', 'trumpet', 
  'hammer', 'paper', 'sunflower',
  'garlic', 'cheese', 'podcast', 'football',
  'television' , 'speaker' , 'coffee' , 'glue', 
  'super cali fragile istic expiala doe shehs' ,
];

function Pokemon( params: AppParams ) {
  /**
   * The pokemon to retrieve
   */
  let [ pokemon, setPokemon ] = useState<string>('pikachu');


  useEffect( () => {
      (async function() {
        const response = await fetch ("https://pokeapi.co/api/v2/pokemon/" + pokemon)
        const data = await response.json();
        console.log(' ZZZ Data', data); 
      })();
    }
  );

  //
  // Callbacks
  //


  function fetchPokemon() {
  }

  return (
    <Container>
      <Typography variant="h2" color="inherit" component="div">
      Pokemon
      </Typography>
      <Button variant='contained' onClick={fetchPokemon}>Get Pokemon</Button>
      <TextField id="standard-basic" label="Standard" value={pokemon} onChange={ ev => setPokemon( ev.target.value) } variant="standard" />
    </Container>
  );
} // END Pokemon
