import { createElement, useState } from 'react';

import { createTheme, Theme, ThemeProvider } from '@mui/material/styles';
import { indigo, orange, lime } from '@mui/material/colors';

import { AppDef } from './apps/apps';
import AppBar from './AppBar';
import Welcome  from './apps/Welcome';
import workingMemoryAppDef  from './apps/working-memory/WorkingMemory';

const theme: Theme = createTheme({
  palette: {
    primary: {
      main: lime[900],
    },
  }
});

const apps: Array<AppDef> = [
  workingMemoryAppDef,
]

export default function Main() {
  const [currentAppId, setCurrentAppId] = useState<string>();
  const [appParams, setAppParams] = useState( {} );

  const component = apps.find(it=>it.id===currentAppId)?.component || Welcome;

  return (
    <ThemeProvider theme={theme}>
      <AppBar
        appDefs={ apps }
        onSelectItemChanged={(appId)=>setCurrentAppId(appId)}
        />
      {createElement( component, appParams )}
    </ThemeProvider>
  );
}
