import { ChangeEvent, useState, useEffect } from 'react';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import * as React from 'react';
import Checkbox from '@mui/material/Checkbox';
import { AppDef, AppParams } from '../apps';
import TextField from '@mui/material/TextField';


import { DataGrid } from '@mui/x-data-grid';

type DataType = {
  name: string,
  stats: Array< {
    base_stat: number,
    effort: number,
    stat: {
      name: string,
      url: string,
    }
  } >,
}

const appDef: AppDef = {
    id: 'pokemon',
    label: 'Pokemon',
    component: Pokemon,
}
export default appDef;

function Pokemon( params: AppParams ) {

  const [ isFetching, setIsFetching ] = useState<boolean>(false);

  /**
   * The pokemon to retrieve
   */
  const [ pokemon, setPokemon ] = useState<string>('pikachu');

  const [ data, setData ] = useState<DataType|null>(null);

  useEffect( () => {
    if ( isFetching ) {
      (async function() {
        const response = await fetch ("https://pokeapi.co/api/v2/pokemon/" + pokemon)
        const data = await response.json();
        setIsFetching( false );
        console.log( 'Setting data', data );
        setData( data );
      })();
    }
  });

  console.log( 'Data', data );

  //
  // Callbacks
  //

  const columns = [
    { field: 'stat.name', headerName: 'Stat' },
    { field: 'base_stat', headerName: 'Base Stat' },
    { field: 'effort', headerName: 'Effort' },
  ]
  return (
    <Container>
      <Typography variant="h2" color="inherit" component="div">
      Pokemon
      </Typography>
      <Button variant='contained' onClick={()=>setIsFetching(true)}>Get Pokemon</Button>
      <TextField id="standard-basic" label="Standard" value={pokemon} onChange={ ev => setPokemon( ev.target.value) } variant="standard" />
    
      { data ?  <div>
        hello
        {/* <DataGrid
          rows={data.stats}
          columns={columns}
          initialState={{
            pagination: {
              paginationModel: { page: 0, pageSize: 5 },
            },
          }}
          pageSizeOptions={[5, 10]}
          checkboxSelection
        /> */}
      </div> : null
      }
    </Container>
  );
} // END Pokemon
