import { ChangeEvent, useState, useEffect } from 'react';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import * as React from 'react';
import Checkbox from '@mui/material/Checkbox';
import { AppDef, AppParams } from '../apps';
import TextField from '@mui/material/TextField';




const appDef: AppDef = {
    id: 'pokemon',
    label: 'Pokemon',
    component: Pokemon,
}
export default appDef;

function Pokemon( params: AppParams ) {

  const [ isFetching, setIsFetching ] = useState<boolean>(false);

  /**
   * The pokemon to retrieve
   */
  const [ pokemon, setPokemon ] = useState<string>('pikachu');

  const [ data, setData ] = useState<object>({});

  useEffect( () => {
    if ( isFetching ) {
      (async function() {
        const response = await fetch ("https://pokeapi.co/api/v2/pokemon/" + pokemon)
        const data = await response.json();
        setIsFetching( false );
        setData( data );
      })();
    }
  });

  //
  // Callbacks
  //

  return (
    <Container>
      <Typography variant="h2" color="inherit" component="div">
      Pokemon
      </Typography>
      <Button variant='contained' onClick={()=>setIsFetching(true)}>Get Pokemon</Button>
      <TextField id="standard-basic" label="Standard" value={pokemon} onChange={ ev => setPokemon( ev.target.value) } variant="standard" />
    </Container>
  );
} // END Pokemon
