import * as React from 'react';
import MuiAppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Button from '@mui/material/Button';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';
import ClickAwayListener from '@mui/material/ClickAwayListener';

type AppBarParams = {
  selectedApp?: string,
  onSelectedAppChange?: ()=>void,
}

export default function AppBar( params: AppBarParams ) {
  return (
    <Box sx={{ flexGrow: 1 }}>
      <MuiAppBar position="static">
        <Toolbar variant="dense">
          <IconButton edge="start" color="inherit" aria-label="menu" sx={{ mr: 2 }}>
            <MenuIcon />
          </IconButton>
          <AppBarMenu id="apps-menu" name='Apps' items={['abc','def']}/>
        </Toolbar>
      </MuiAppBar>
    </Box>
  );
}

type AppBarMenuParams = {
  id: string,
  name: string,
  items?: string[],
};

function AppBarMenu( params: AppBarMenuParams ) {

  const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null);
  const open = Boolean(anchorEl);

  const handleClick = (event: React.MouseEvent<HTMLButtonElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const menuId = `${params.id}-basic-menu`;

  return (
    <div>
      <Button
        variant="outlined"
        id={ `${params.id}-basic-button` }
        sx={{ my: 2, color: 'white', display: 'block' }}
        aria-haspopup="true"
        aria-controls={open ? menuId : undefined}
        aria-expanded={open ? 'true' : undefined}
        onClick={handleClick}
      >
        {params.name}
      </Button>
      { params.items?.length &&
        <Menu
          id={menuId}
          anchorEl={anchorEl}
          open={open}
          onClose={handleClose}
          MenuListProps={{
            'aria-labelledby': 'basic-button',
          }}
        >
          {params.items?.map( item => <MenuItem onClick={handleClose}>{item}</MenuItem>)}
        </Menu>
      }
    </div>

  );
}
