import { ReactElement, useState } from 'react';
import { AppParams, AppDef } from './apps/apps';
import AppBar from './AppBar';

import workingMemoryAppDef  from './apps/working-memory/WorkingMemory';
import Welcome  from './apps/Welcome';

type MainParams = {
  appName: string,
}

const apps: Array<AppDef> = [
  workingMemoryAppDef,
]

export default function Main() {
  const [currentAppName, setCurrentAppName] = useState( apps[0]?.id ?? '' );

  const [appParams, setAppParams] = useState( {} );

  const component = apps.find(it=>it.id===currentAppName)?.component || Welcome;

  return (
    <div>
      <AppBar appDefs={ apps }/>
      {component( appParams )}
    </div>
  );
}
