import { useState } from 'react';
import Button from '@mui/material/Button';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import * as React from 'react';
import Checkbox from '@mui/material/Checkbox';
import { AppDef, AppParams } from '../apps';
import TextField from '@mui/material/TextField';




const appDef: AppDef = {
    id: 'working-memory',
    label: 'Working Memory',
    component: WorkingMemory,
}
export default appDef;

const masterList = [
  'apple', 'eggs', 'elephant', 'trumpet', 
  'hammer', 'paper', 'sunflower',
  'garlic', 'cheese', 'podcast', 'football',
  'television' , 'speaker' , 'coffee' , 'glue', 
  'super cali fragile istic expiala doe shehs' ,
];

function WorkingMemory( params: AppParams ) {
  /**
   * The available words left for picking
   */
  const [ availableWords, setAvailableWords ] = useState<Set<string>>(new Set( masterList ) );

  /**
   * Number of words left to add to the list.
   */
  const [ numRemaining, setNumRemaining ] = useState<number>(0);

  /**
   * The words to remember
   */
  const [ words, setWords ] = useState<string[]>( [] );

  /**
   * Should show the whole list.
   */
  const [ shouldShowWords, setShouldShowWords ] = useState<boolean>( false );

  /**
   * Picks the next word to add to the list.
   */

  const synth = window.speechSynthesis;
  const utterance = new SpeechSynthesisUtterance();
  


  function pickNextWord() {
    if ( availableWords.size > 0 ) {
      const word = pickElement( Array.from(availableWords) );
      availableWords.delete( word );
      setAvailableWords( availableWords );
      textToSpeech(word);
      return word;
    }
  }
  let helloWorld = 'Hello World'
  function textToSpeech(helloWorld: string) {
    utterance.text = helloWorld;
    synth.speak(utterance);
  }  
  //
  // Callbacks
  //

  function startShowingWords() {

    setNumRemaining(7);
    setWords( [ pickNextWord() ] );
  }

  function showNextWord() {
    setWords( currentWords => [ ...currentWords, pickNextWord() ] );
    setNumRemaining( n => n - 1 );
  }

  function resetEverything() {
    setAvailableWords( new Set( masterList ) );
    setShouldShowWords( false );
    setWords( [] );
  }

  return (
    <Container>
      <Typography variant="h2" color="inherit" component="div">
      Working Memory
      </Typography>
      {
        words.length === 0 && numRemaining === 0 ?
          <Button variant='contained' onClick={startShowingWords}>Start</Button>
        : numRemaining > 0 ?
          <Button variant='contained' onClick={showNextWord}>Next word</Button>
        : !shouldShowWords ?
          <Button variant='contained' onClick={ () => setShouldShowWords(true)}>Show answers</Button>
        : <Button variant='contained' onClick={resetEverything}>Reset</Button>
      }
      {
        numRemaining >= 0 && !shouldShowWords && <Typography variant="h4">{words[ words.length - 1]}</Typography> 
      }
      {
        shouldShowWords && words.map( word => <Typography variant="h5">{word}</Typography> )
      }
       <TextField id="standard-basic" label="Standard" value="abcd " variant="standard" />
    </Container>
  );
} // END WorkingMemory

function pickElement( theArray: Array<any> ) {
  const index = pickIndex( theArray );
  return theArray[ index ];
}

function pickIndex( theArray: Array<any> ) {
  return theArray.length > 0 ? Math.floor( Math.random() * theArray.length ) : -1
}
