import { useState } from 'react';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { AppDef, AppParams } from '../apps';

const appDef: AppDef = {
    id: 'working-memory',
    label: 'Working Memory',
    component: WorkingMemory,
}
export default appDef;

const masterList = [
  'apple', 'eggs', 'elephant', 'trumpet', 
  'hammer', 'paper', 'sunflower',
];

function WorkingMemory( params: AppParams ) {
  const [ numRemaining, setNumRemaining ] = useState<number>(0);
  const [ words, setWords ] = useState<string[]>( [] );
  const [ shouldShowWords, setShouldShowWords ] = useState<boolean>( false );

  function populateList() {
    setNumRemaining( 3 );
    setWords( [] );
  }

  function showNextWord() {
    setWords( currentWords => [ ...currentWords, masterList[ numRemaining ] ] );
    setNumRemaining( n => n - 1 );
  }

  return (
    <div>
      <Typography variant="h2" color="inherit" component="div">
      Working Memory
      </Typography>
    </div>
  );
}
