import { useState } from 'react';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { AppDef, AppParams } from '../apps';

const appDef: AppDef = {
    id: 'working-memory',
    label: 'Working Memory',
    component: WorkingMemory,
}
export default appDef;

const masterList = [
  'apple', 'eggs', 'elephant', 'trumpet', 
  'hammer', 'paper', 'sunflower',
];

function WorkingMemory( params: AppParams ) {
  const [ numRemaining, setNumRemaining ] = useState<number>(0);
  const [ words, setWords ] = useState<string[]>( [] );
  const [ shouldShowWords, setShouldShowWords ] = useState<boolean>( false );

  function pickNextWord() {

    return masterList[ numRemaining ];
  }

  function startShowingWords() {
    setShouldShowWords( false );
    setNumRemaining( 3 );
    setWords( [ pickNextWord() ] );
  }

  function showNextWord() {
    setWords( currentWords => [ ...currentWords, pickNextWord() ] );
    setNumRemaining( n => n - 1 );
  }

  return (
    <div>
      <Typography variant="h2" color="inherit" component="div">
      Working Memory
      </Typography>
      {
        words.length === 0 && numRemaining === 0 ?
          <Button onClick={startShowingWords}>Start</Button>
        : numRemaining > 0 ?
          <Button onClick={showNextWord}>Show next word</Button>
        : !shouldShowWords ?
          <Button onClick={ () => setShouldShowWords(true)}>Show answers</Button>
        : <Button onClick={startShowingWords}>Re-start</Button>
      }
      {
        numRemaining > 0 && <Typography variant="h4">{words[ words.length - 1]}</Typography> 
      }
      {
        shouldShowWords && words.map( word => <Typography variant="h5">{word}</Typography> )
      }
    </div>
  );
}

function pickIndex( theArray: Array<any> ) {
  return theArray.length > 0 ? Math.floor( Math.random() * theArray.length ) : -1
}
